import React, { Component } from "react";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBCol,
  MDBTable,
  MDBRating,
} from "mdbreact";
import axios from "axios";

class StatisticsForProducts extends Component {
  state = {
    scores: [],
    comments: [],
    statistics: [],
    fetch: true,
  };

  getComments = async (product_id) => {
    // fetch comments
    var url = `http://localhost:3000/comments/byProduct/${product_id}`;
    const comments = await axios.get(url);
    await this.setState({ comments: comments.data, fetch: false });

    // creating only score array
    comments.data.forEach((comment) => {
      this.state.scores.push(comment.score);
    });

    console.log(this.state.scores);

    // // avaraging
    // this.state.scores.forEach(complexScore)
    // {
    //   complexScore.forEach(score => {
    //     if(score.title)
    //   })
    // }
  };

  render() {
    if (this.props.product_id !== undefined && this.state.fetch) {
      this.getComments(this.props.product_id);
    }

    while (!this.state.scores.value);
    return (
      <div>
        <MDBCard>
          <MDBTable>
            <MDBRow>
              <p className="text-center">סטטיסטיקות</p>
            </MDBRow>
            {this.state.scores
              .sort((a, b) => a.value >= b.value)
              .map((rate) => (
                <div className="ml-5">
                  <MDBRow>
                    <MDBCardTitle>{rate.title}</MDBCardTitle>
                    <MDBRating
                      fillColors={[
                        "red-text",
                        "orange-text",
                        "yellow-text",
                        "lime-text",
                        "light-green-text",
                      ]}
                      data={[
                        {
                          tooltip: "Bad",
                          choosed: rate.value >= 1 && rate.value < 2,
                        },
                        {
                          tooltip: "Poor",
                          choosed: rate.value >= 2 && rate.value < 3,
                        },
                        {
                          tooltip: "Ok",
                          choosed: rate.value >= 3 && rate.value < 4,
                        },
                        {
                          tooltip: "Nice",
                          choosed: rate.value >= 4 && rate.value < 5,
                        },
                        {
                          tooltip: "Excellent",
                          choosed: rate.value == 5,
                        },
                      ]}
                    />
                  </MDBRow>
                </div>
              ))}
          </MDBTable>
        </MDBCard>
      </div>
    );
  }
}

export default StatisticsForProducts;
