import React, { Component } from "react";
import axios from "axios";

class AddComment extends React.Component {
  state = {};

  comment = {
    product_id: 2,
    user_id: 12,
    title: "temp message",
    message: "blabla",
    score: "5",
  };

  postComment = async (comment) => {
    const res = axios.post("http//localhost:3000/comments", comment);

    console.log(res);
    this.state.comment = res;
  };

  render() {
    return <div>{this.state.comment.product_id}</div>;
  }
}

export default AddComment;
