import axios from "axios";

export async function addComment(comment) {
  return await axios.post("http://localhost:3000/comments", {
    product_id: comment.product_id,
    user_id: 2,
    user_name: comment.user_name,
    message: comment.message,
    score: comment.score,
  });
}

export async function getAllProducts() {
  return await axios.get("http://localhost:3000/products/all");
}

export async function getAllCompenies() {
  return await axios.get("http://localhost:3000/compenies");
}

export async function getCommentsByID(product_id) {
  var url = `http://localhost:3000/comments/byProduct/${product_id}`;
  return await axios.get(url);
}

export async function signIn(email, password) {
  return await axios.post("http://localhost:3000/auth", {
    email: email,
    password: password,
  });
}

export async function signUp(firstName, email, password, phone) {
  return axios.post("http://localhost:3000/users", {
    name: firstName,
    email: email,
    password: password,
    phone: phone,
  });
}

export async function getProductByID(product_id) {
  return axios.get(`http://localhost:3000/products/${product_id}`);
}

export default {
  addComment,
  getAllProducts,
  getAllCompenies,
  getCommentsByID,
  signIn,
  signUp,
  getProductByID,
};
