import React, { Component } from "react";
import axios from "axios";
import { MDBBtn, MDBContainer, MDBCard, MDBInput } from "mdbreact";

class AddComment extends React.Component {
  state = {};

  comment = {
    product_id: this.props.product_id,
    user_id: 12,
    title: "temp message",
    message: "blabla",
    score: "5",
  };

  postComment = async () => {
    console.log("in");
    const res = await axios.post(
      "http://localhost:3000/comments",
      this.comment
    );

    console.log(res);
    this.state = { ...res };
  };

  render() {
    console.log();
    return (
      <MDBCard>
        <MDBContainer id="main">
          <MDBInput type="text" value="כותרת" size="1"></MDBInput>
          <MDBInput type="text" value="גוף הודעה" size="1"></MDBInput>
          <MDBBtn onClick={this.postComment}>הגב</MDBBtn>
        </MDBContainer>
      </MDBCard>
    );
  }
}

export default AddComment;
