import React, { Component } from "react";
import axios from "axios";
import Card from "./Card";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBCol,
} from "mdbreact";

class CardGallery extends Component {
  state = {
    products: [],
  };

  async componentDidMount() {
    const products = await axios.get("http://localhost:3000/products/all");
    this.setState({ products: products.data });
  }

  render() {
    return (
      <div id="CardsGallery">
        <h1 className="h1-responsive font-weight-bold mt-sm-5 text-center">
          המוצרים המומצלים ביותר
        </h1>
        <MDBRow>
          {this.state.products.map((product) => (
            <MDBCol>
              <Card {...product} />
              {/* <src={product.BoxImg
                  <MDBCardTitle>{product.name}</MDBCardTitle>
                  <MDBCardText>price is: {product.price}</MDBCardText>
                  <MDBBtn href={"/product/" + product._id}>לצפייה</MDBBtn> */}
            </MDBCol>
          ))}
        </MDBRow>
      </div>
    );
  }
}

export default CardGallery;
