import React from "react";
import axios from "axios";
import { MDBBtn, MDBContainer, MDBCard, MDBInput } from "mdbreact";

class AddComment extends React.Component {
  state = {
    comment: {
      title: "",
      message: "",
      score: "",
    },
  };

  postComment = async () => {
    console.log("in");
    const res = await axios.post(
      "http://localhost:3000/comments",
      this.comment
    );

    console.log(res);
    this.state = { ...res };
  };

  handleSubmit = async (e) => {
    e.preventDefault();

    const { addComment } = this.props;
    addComment({
      _id: null,
      product_id: this.props.product_id,
      user_id: 2,
      title: this.state.comment.title,
      message: this.state.comment.message,
      score: this.state.comment.score,
      date: "now",
    });

    try {
      const response = await axios.post("http://localhost:3000/comments", {
        product_id: this.props.product_id,
        user_id: 2,
        title: this.state.comment.title,
        message: this.state.comment.message,
        score: this.state.comment.score,
      });

      //save and alert
      alert(response.data);
    } catch (e) {
      alert(e);
    }
  };

  handleChange = (e) => {
    const comment = { ...this.state.comment };
    comment[e.currentTarget.name] = e.currentTarget.value;
    this.setState({ comment });
    console.log(comment);
  };

  render() {
    return (
      <MDBCard>
        <h1 className=" position-relative text-center">הוסף תגובה</h1>
        <MDBContainer id="main">
          <MDBInput
            type="text"
            ref={this.title}
            name="title"
            value={this.state.comment.title}
            onChange={this.handleChange}
          ></MDBInput>
          <MDBInput
            type="text"
            ref={this.title}
            name="message"
            value={this.state.comment.message}
            onChange={this.handleChange}
          ></MDBInput>
          <MDBInput
            type="text"
            ref={this.title}
            name="score"
            value={this.state.comment.score}
            onChange={this.handleChange}
          ></MDBInput>
          <div className="text-center">
            <MDBBtn className="text-center" onClick={this.handleSubmit}>
              הגב
            </MDBBtn>
          </div>
        </MDBContainer>
      </MDBCard>
    );
  }
}

export default AddComment;
