import React from "react";
import Footer from "../components/Footer";
import {
  MDBMask,
  MDBRow,
  MDBCol,
  MDBBtn,
  MDBView,
  MDBContainer,
  MDBAnimation,
  MDBCard,
} from "mdbreact";
import "../styles/HomePage.css";

class AppPage extends React.Component {
  state = {
    collapsed: false,
  };

  handleTogglerClick = () => {
    const { collapsed } = this.state;
    this.setState({
      collapsed: !collapsed,
    });
  };

  componentDidMount() {
    document.querySelector("nav").style.height = "65px";
  }

  componentWillUnmount() {
    document.querySelector("nav").style.height = "auto";
  }

  render() {
    const { collapsed } = this.state;

    const overlay = (
      <div
        id="sidenav-overlay"
        style={{ backgroundColor: "transparent" }}
        onClick={this.handleTogglerClick}
      />
    );
    return (
      <body>
        <div id="first">
          <MDBView>
            <MDBMask className="white-text gradient" />
            <MDBContainer className="d-flex white-text mt-10">
              <div className="flex-row-reverse">
                {/* <MDBRow end>
                  <MDBAnimation type="fadeInRight" delay=".3s">
                    <h1 className="h1-responsive font-weight-bold mt-sm-5">
                      Canabiz ברוכים הבאים לאתר
                    </h1>
                    <hr
                      className="hr-dark
                    "
                    />
                    <h6 className="mb-4">
                      באתר תוכלו לקבל את המידע העדכני ביותר אודות הזנים המתאים
                      ביותר עבורכם
                    </h6>
                    <MDBBtn href="/signin" color="white">
                      כניסה
                    </MDBBtn>
                    <MDBBtn href="/signup" color="white">
                      הרשמה
                    </MDBBtn>
                  </MDBAnimation>
                </MDBRow> */}
                <MDBRow end>
                  <MDBCol xl="3">
                    <MDBAnimation type="lightSpeedIn" delay=".4s">
                      <img
                        src="https://upload.wikimedia.org/wikipedia/commons/6/67/Medical_doctor.png"
                        alt=""
                        className="img-fluid"
                      />
                    </MDBAnimation>
                  </MDBCol>
                </MDBRow>
              </div>
            </MDBContainer>
          </MDBView>
        </div>
        <div id="second"></div>
        <Footer></Footer>
      </body>
    );
  }
}

export default AppPage;
