import React, { Component } from "react";
import axios from "axios";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBCol,
} from "mdbreact";

class CardExample extends Component {
  state = {
    products: [],
  };

  async componentDidMount() {
    const products = await axios.get("http://localhost:3000/products/all");
    this.setState({ products: products.data });
  }

  render() {
    return (
      <div className="justify-content-center">
        <h1 className="h1-responsive font-weight-bold text-center">
          המוצרים המומצלים ביותר
        </h1>
        <div className="w-75 p-3 text-center">
          <MDBRow className="row d-flex justify-content-center">
            {this.state.products.map((product) => (
              <MDBCol>
                <MDBCard className="mh-50 h-75 align-middle">
                  <div class="text-center">
                    <MDBCardImage
                      cascade
                      className="img-fluid"
                      src={product.BoxImg}
                    />
                  </div>
                  <MDBCardBody cascade>
                    <MDBCardTitle>{product.name}</MDBCardTitle>
                    <MDBCardText>{product.productDescription}</MDBCardText>
                    <MDBBtn href={"/product/" + product._id}>לצפייה</MDBBtn>
                  </MDBCardBody>
                </MDBCard>
              </MDBCol>
            ))}
          </MDBRow>
        </div>
      </div>
    );
  }
}

export default CardExample;
