import React, { Component, useState } from "react";
import axios from "axios";
import _ from "lodash";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBCol,
  MDBTable,
  MDBIcon,
} from "mdbreact";
import "../styles/ImagesWithCards.css";
import MultiSelect from "react-multiple-select-dropdown-lite";
import "react-multiple-select-dropdown-lite/dist/index.css";

class CardExample extends Component {
  state = {
    allProducts: [],
    products: [],
    allComments: [],
    allCompenies: [],

    query: {
      ish: ["indica", "sativa", "hybrid"],
      thc: ["20", "15", "10"],
      companies: [],
    },

    options: {
      ish: [
        { label: "אינדיקה", value: "indica" },
        { label: "סאטיבה", value: "sativa" },
        { label: "הייבריד", value: "hybrid" },
      ],
      thc: [
        { label: "T/20", value: "20" },
        { label: "T/15", value: "15" },
        { label: "T/10", value: "10" },
      ],
      companies: [],
    },
  };

  async componentDidMount() {
    const allProducts = await axios.get("http://localhost:3000/products/all");
    await this.setState({
      allProducts: allProducts.data,
      products: allProducts.data,
    });

    const allCompenies = await axios.get("http://localhost:3000/compenies");
    allCompenies.data.forEach((company) => {
      this.state.options.companies.push({
        label: company.name,
        value: company.name,
      });

      this.state.query.companies.push(company.name);
    });
    this.state.allCompanies = this.state.query.companies;
  }

  initQuery = () => {
    this.state.query.ish = ["indica", "sativa", "hybrid"];
    this.state.query.thc = ["20", "15", "10"];
    this.state.query.companies = this.state.allCompenies;
  };

  ish_btnClicked = async (val) => {
    if (!val) {
      this.state.query.ish = ["indica", "sativa", "hybrid"];
    } else {
      this.state.query.ish = val.split(",");
    }
    this.generateProductList();
  };

  thc_btnClicked = async (val) => {
    if (!val) {
      this.state.query.thc = ["20", "15", "10"];
    } else {
      this.state.query.thc = val.split(",");
    }
    this.generateProductList();
  };

  company_btnClicked = async (val) => {
    if (!val) {
      console.log("h");
      this.state.query.companies = this.state.allCompenies;
    } else {
      this.state.query.companies = val.split(",");
    }
    this.generateProductList();
  };

  generateProductList = () => {
    var productsAfterQuery = this.state.allProducts.filter((product) => {
      return (
        _.includes(this.state.query.ish, product.ish) &&
        _.includes(this.state.query.thc, product.thc) &&
        _.includes(this.state.query.companies, product.company)
      );
    });

    this.setState({ products: productsAfterQuery });
  };

  render() {
    return (
      <div id="images_with_cards">
        <div style={{ margin: "auto" }}>
          <MultiSelect
            onChange={this.ish_btnClicked}
            options={this.state.options.ish}
          />
          <MultiSelect
            onChange={this.thc_btnClicked}
            options={this.state.options.thc}
          />
          <MultiSelect
            onChange={this.company_btnClicked}
            options={this.state.options.companies}
          />
        </div>

        {/* <div id="filter">
          <MDBCard>
            <div id="header">
              <h1 className="black-text">סנן מוצרים</h1>
            </div>
            <div id="allFiltersButtons">
              <MDBRow>
                <div id="filterCard1">
                  <MDBCard color="green" className="m-3">
                    <MDBBtn
                      innerRef={this.buttonRef}
                      color="white"
                      className="btn-sm"
                      name="sativa"
                      onClick={this.ish_btnClicked}
                    >
                      סאטיבה
                    </MDBBtn>
                    <MDBBtn
                      color="white"
                      className="btn-sm"
                      name="indica"
                      onClick={this.ish_btnClicked}
                    >
                      אינדיקה
                    </MDBBtn>

                    <MDBBtn
                      color="white"
                      className="btn-sm"
                      name="hybrid"
                      onClick={this.ish_btnClicked}
                    >
                      הייבריד
                    </MDBBtn>
                  </MDBCard>
                </div>
                <div id="filterCard2">
                  <MDBCard color="green" className="m-3">
                    <MDBBtn
                      color="white"
                      className="btn-sm"
                      name="20"
                      onClick={this.thc_btnClicked}
                    >
                      THC - 20
                    </MDBBtn>
                    <MDBBtn
                      color="white"
                      className="btn-sm"
                      name="15"
                      onClick={this.thc_btnClicked}
                    >
                      THC - 15
                    </MDBBtn>
                    <MDBBtn
                      color="white"
                      className="btn-sm"
                      name="10"
                      onClick={this.thc_btnClicked}
                    >
                      THC - 10
                    </MDBBtn>
                  </MDBCard>
                </div>
                <div id="filterCard3">
                  <MDBCard color="green" className="m-3">
                    {this.state.allCompenies.map((company, index) => (
                      <MDBBtn
                        id={index}
                        color="white"
                        className="btn-sm"
                        name={company}
                        onClick={this.company_btnClicked}
                      >
                        {company}
                      </MDBBtn>
                    ))}
                  </MDBCard>
                </div>
                <div id="filterCard">
                  <MDBCard color="red" className="m-3">
                    <MDBBtn
                      color="white"
                      className="btn-sm"
                      onClick={this.resetState}
                    >
                      איפוס
                    </MDBBtn>
                  </MDBCard>
                </div>
              </MDBRow>
            </div>
          </MDBCard>
        </div> */}

        <div id="products">
          <MDBTable>
            <MDBRow>
              {this.state.products.map((product, index) => (
                <MDBCol key={index}>
                  <div id="singleCard">
                    <MDBCard id={index}>
                      <MDBCardImage
                        className="img-fluid"
                        src={product.BoxImg}
                      />
                      <MDBCardBody cascade>
                        <MDBCardTitle>
                          {product.name} : ({product.company})
                        </MDBCardTitle>
                        <MDBCardTitle>
                          {" "}
                          {product.ish} T{product.thc}\C
                          {product.cbd}
                        </MDBCardTitle>
                        <MDBCardText id="description">
                          {product.productDescription}
                        </MDBCardText>
                        <MDBBtn id="btn" href={"/product/" + product._id}>
                          לצפייה
                        </MDBBtn>
                      </MDBCardBody>
                    </MDBCard>
                  </div>
                </MDBCol>
              ))}
            </MDBRow>
          </MDBTable>
        </div>
      </div>
    );
  }
}

export default CardExample;
