import React, { Component } from "react";
import axios from "axios";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBCol,
} from "mdbreact";

class CardExample extends Component {
  state = {
    products: [],
  };

  async componentDidMount() {
    const products = await axios.get("http://localhost:3000/products/all");
    //console.log(products.data[0].price);
    this.setState({ products: products.data });
  }

  render() {
    return (
      <div>
        <h1 className="h1-responsive font-weight-bold mt-sm-5 text-center">
          המוצרים המומצלים ביותר
        </h1>
        <MDBRow>
          {this.state.products.map((product) => (
            <MDBCol>
              <MDBCard wide>
                <MDBCardImage
                  cascade
                  className="img-fluid"
                  src="https://mdbootstrap.com/img/Photos/Horizontal/People/6-col/img%20%283%29.jpg"
                />
                <MDBCardBody cascade>
                  <MDBCardTitle>{product.name}</MDBCardTitle>
                  <MDBCardText>price is: {product.price}</MDBCardText>
                  <MDBBtn href={"/product/" + this.state.products[name]}>
                    לצפייה
                  </MDBBtn>
                </MDBCardBody>
              </MDBCard>
            </MDBCol>
          ))}
        </MDBRow>
      </div>
    );
  }
}

export default CardExample;
