import React from "react";
import {
  MDBMask,
  MDBRow,
  MDBCol,
  MDBBtn,
  MDBView,
  MDBContainer,
  MDBAnimation,
  MDBCard,
} from "mdbreact";
import "../styles/HomePage.css";
import axios from "axios";
import ReactCardCarousel from "react-card-carousel";

class AppPage extends React.Component {
  state = {
    someProducts: [],
  };

  async componentDidMount() {
    const allProducts = await axios.get("http://localhost:3000/products/all");
    await this.setState({
      someProducts: allProducts.data,
    });
  }

  CARD_STYLE = () => {
    return {
      height: "200px",
      width: "200px",
      paddingTop: "80px",
      textAlign: "center",
      background: "#52C0F5",
      color: "#FFF",
      fontSize: "12px",
      textTransform: "uppercase",
      borderRadius: "10px",
    };
  };

  render() {
    return (
      <body>
        <div id="first">
          <div id="welcom">
            <MDBAnimation type="fadeInRight" delay=".3s">
              <h1 className="h1-responsive font-weight-bold mt-sm-5">
                ברוכים הבאים לאתר קנאביז
              </h1>

              <h6 className="mb-4">
                באתר תוכלו לקבל את המידע העדכני ביותר אודות הזנים המתאים ביותר
                עבורכם
              </h6>
              <MDBBtn href="/signin" color="white">
                כניסה
              </MDBBtn>
              <MDBBtn href="/signup" color="white">
                הרשמה
              </MDBBtn>
            </MDBAnimation>
          </div>
        </div>
        <div id="second">
          <div id="whoRwe">
            <h1>מי אנחנו?</h1>
            <h2>
              בעידן שבו זני הקנאביס משתנים מרגע לרגע, חברות הקנאביס אין עקביות
              בזני ובאופי האצווה
            </h2>
          </div>
        </div>
        <div id="third">
          <ReactCardCarousel autoplay={true} autoplay_speed={2500}>
            <div style={this.CARD_STYLE}>First Card</div>
            <div style={this.CARD_STYLE}>Second Card</div>
            <div style={this.CARD_STYLE}>Third Card</div>
            <div style={this.CARD_STYLE}>Fourth Card</div>
            <div style={this.CARD_STYLE}>Fifth Card</div>
          </ReactCardCarousel>
        </div>
        <div id="fourth"></div>
      </body>
    );
  }
}

export default AppPage;
