import React from "react";
import { MDBRow, MDBCard, MDBCardBody, MDBIcon } from "mdbreact";
import SingleSocialComment from "./SingleSocialComment";
import axios from "axios";

class SocialSingleComment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      retriveData: true,
      comments: {},
    };
  }

  getComments = async (product_id) => {
    var url = `http://localhost:3000/comments/byProduct/1`;
    const comments = await axios.get(url);

    this.setState({ comments: comments.data });
    console.log(await this.state.comments); // check
  };

  render() {
    if (this.state.retriveData) {
      this.getComments(this.props.product_id);

      this.state.retriveData = false;
      console.log(this.state.comments);
    }

    return (
      <MDBCard
        className="my-5 px-5 pt-4"
        style={{ fontWeight: 300, maxWidth: 600 }}
      >
        {Object.keys(this.state.comments).map((comment) => (
          <MDBCardBody className="py-0">
            <MDBRow>
              <SingleSocialComment {...comment} />
            </MDBRow>
          </MDBCardBody>
        ))}
      </MDBCard>
    );
  }
}

export default SocialSingleComment;
