import React from "react";
import {
  MDBMask,
  MDBRow,
  MDBCol,
  MDBBtn,
  MDBView,
  MDBContainer,
  MDBAnimation,
} from "mdbreact";
import "../styles/HomePage.css";

class AppPage extends React.Component {
  state = {
    collapsed: false,
  };

  handleTogglerClick = () => {
    const { collapsed } = this.state;
    this.setState({
      collapsed: !collapsed,
    });
  };

  componentDidMount() {
    document.querySelector("nav").style.height = "65px";
  }

  componentWillUnmount() {
    document.querySelector("nav").style.height = "auto";
  }

  render() {
    const { collapsed } = this.state;

    const overlay = (
      <div
        id="sidenav-overlay"
        style={{ backgroundColor: "transparent" }}
        onClick={this.handleTogglerClick}
      />
    );
    return (
      <body>
        <div id="imageDiv">
          <img id="firstImage"></img>
        </div>
        <div id="maindiv">
          <MDBView>
            <MDBMask className="white-text gradient" />
            <MDBContainer
              style={{ height: "100%", width: "100%", paddingTop: "10rem" }}
              className="d-flex justify-content-center white-text align-items-center"
            >
              <MDBRow>
                <MDBCol
                  md="6"
                  className="text-center text-md-left mt-xl-5 mb-5"
                >
                  <MDBAnimation type="fadeInLeft" delay=".3s">
                    <h1 className="h1-responsive font-weight-bold mt-sm-5">
                      Canabiz ברוכים הבאים לאתר
                    </h1>
                    <hr className="hr-light" />
                    <h6 className="mb-4">
                      באתר תוכלו לקבל את המידע העדכני ביותר אודות הזנים המתאים
                      ביותר עבורכם
                    </h6>
                    <MDBBtn href="/signin" color="white">
                      כניסה
                    </MDBBtn>
                    <MDBBtn href="/signup" color="white">
                      הרשמה
                    </MDBBtn>
                  </MDBAnimation>
                </MDBCol>

                <MDBCol md="6" xl="3" className="mt-xl-5">
                  <MDBAnimation type="fadeInRight" delay=".3s">
                    <img
                      src="https://upload.wikimedia.org/wikipedia/commons/6/67/Medical_doctor.png"
                      alt=""
                      className="img-fluid"
                    />
                  </MDBAnimation>
                </MDBCol>
              </MDBRow>
            </MDBContainer>
          </MDBView>
        </div>
      </body>
    );
  }
}

export default AppPage;
