import React from "react";
import { MDBRow, MDBCard, MDBCardBody } from "mdbreact";
import SingleSocialComment from "./SingleSocialComment";
import axios from "axios";
import AddComment from "./AddComment";

class SocialNewsFeed extends React.Component {
  constructor(props) {
    super(props);

    this.state = { comments: [] };
  }

  // //need to change dynamicly
  // async componentDidMount() {
  //   await this.getComments("5f5befa2a630fd4da0f2707f");
  // }

  // getComments = async (product_id) => {
  //   var url = `http://localhost:3000/comments/byProduct/${product_id}`;
  //   const comments = await axios.get(url);

  //   await this.setState({ comments: comments.data });
  // };

  render() {
    if (this.props[0] != undefined) {
      if (this.props[0]._id != undefined) {
        var comments = [];
        comments = this.props;
        console.log(comments[0]);
        return (
          <div>
            <AddComment {...this.props} />
            <MDBCard
              className="my-5 px-5 pt-4"
              style={{ fontWeight: 300, maxWidth: 600 }}
            >
              {this.props.comments.map((comment) => (
                <MDBCardBody className="py-1 px-md-1">
                  <MDBRow>
                    <SingleSocialComment {...comment} />
                  </MDBRow>
                </MDBCardBody>
              ))}
            </MDBCard>
          </div>
        );
      }
    } else {
      console.log("first");
      return <div>no comments</div>;
    }
  }
}

export default SocialNewsFeed;
