import React, { Component } from "react";
import { MDBCard, MDBIcon, MDBRating } from "mdbreact";

class SingleSocialComment extends Component {
  state = {};

  render() {
    console.log(this.props);
    return (
      <MDBCard className="mdb-feed py-3 px-md-5">
        <div className="news">
          <div className="label">
            <img
              src="https://mdbootstrap.com/img/Photos/Avatars/avatar-1-mini.jpg"
              alt=""
              className="rounded-circle z-depth-1-half"
            />
          </div>
          <div className="excerpt">
            <div className="brief">
              <a href="#!" className="name">
                User id : {this.props.user_id}
              </a>{" "}
              <div className="date">{this.props.title}</div>
              <div className="date">{this.props.message}</div>
            </div>
            <div className="feed-footer">
              <a href="#!" className="like">
                <MDBIcon icon="heart" />
                {/* {this.props.score.map((scoreElement) => {
                  <h1>
                    {scoreElement.title}
                    {scoreElement.value}
                  </h1>;
                })} */}
              </a>
              <div>{this.props.date}</div>
              <MDBRating></MDBRating>
            </div>
          </div>
        </div>
      </MDBCard>
    );
  }
}

export default SingleSocialComment;
