import React, { Component } from "react";
import { MDBContainer as div } from "mdbreact";
import "./index.css";
import Navbar from "./components/Navbar";
import { Route, Switch, Redirect } from "react-router-dom";
import NotFound from "./pages/NotFoundPage";
import jwtDecode from "jwt-decode";
import axios from "axios";

// pages
import ProductsPage from "./pages/ProductsPage";
import ProductPage from "./pages/ProductPage";
import SignInPage from "./pages/SignInPage";
import SignUpPage from "./pages/SignUpPage";
import HomePage from "./pages/HomePage";
import DAdvicePage from "./pages/DAdvicePage";

class App extends Component {
  state = {};

  componentDidMount() {
    let jwt = localStorage.getItem("token");
    const user = jwtDecode(jwt);
    console.log(user);
    while (!this.getUserData()) {
      console.log("i");
    }
  }

  getUserData = async (_id) => {
    var url = `http://localhost:3000/users`;
    const { data: user } = await axios.get(url, { user_id: _id });
    this.setState({ user });

    return user;
  };

  render() {
    return (
      <div>
        <Navbar />
        <div className="content">
          <Switch>
            <Route path="/product/:id" component={ProductPage} />
            <Route path="/products" component={ProductsPage} />
            <Route path="/signin" component={SignInPage} />
            <Route path="/signup" component={SignUpPage} />
            <Route path="/not-found" component={NotFound} />
            <Route path="/DAdvice" component={DAdvicePage} />
            <Route path="/" exact component={HomePage} />
            <Redirect to="/not-found" />
          </Switch>
        </div>
      </div>
    );
  }
}

export default App;
