  render() {
    this.getComments();

    return (
      <body className="bg">
        <div id="productpage">
          <ProductView {...this.state.product} />
          <div id="social_news_feed">
            <StatisticsForProducts
              commentsCategories={this.state.commentsCategories}
              product_id={this.state.product._id}
            />
            <SocialNewsFeed
              product_id={this.state.product._id}
              commentsCategories={this.state.commentsCategories}
            />
          </div>
        </div>
      </body>
    );
  }
}

export default ProductPage;
