import React, { Component } from "react";
import axios from "axios";

class CommentsForProducts extends Component {
  state = {
    comments: {},
  };

  async componentDidMount() {
    //product_id = "5f5d3cb91d01a8e20cd461dd"; // need to be dynamic from props
    var url = `http://localhost:3000/comments/byProduct/5f5d3cb91d01a8e20cd461dd`;
    console.log(this);
    const comments = await axios.get(url);

    this.setState({ comments: comments.data[0] });
  }

  render() {
    return <div>{this.props.name}</div>;
  }
}

export default CommentsForProducts;
