import { MDBCard, MDBRating } from "mdbreact";
import React, { Component } from "react";

class StatisticsForProducts extends Component {
  state = {
    rates: [
      { category: "Taste", score: 5 },
      { category: "Smell", score: 4 },
    ],
  };
  render() {
    return (
      <div>
        <MDBCard>
          {this.state.products.map((product) => (
            <MDBCol>
              <MDBCard id="card">
                <MDBCardImage className="img-fluid" src={product.BoxImg} />
                <MDBCardBody cascade>
                  <MDBCardTitle>{product.name}</MDBCardTitle>
                  <MDBCardText id="description">
                    {product.productDescription}
                  </MDBCardText>
                  <MDBBtn id="btn" href={"/product/" + product._id}>
                    לצפייה
                  </MDBBtn>
                </MDBCardBody>
              </MDBCard>
            </MDBCol>
          ))}
        </MDBCard>
      </div>
    );
  }
}

export default StatisticsForProducts;
