import React from "react";
import axios from "axios";
import {
  MDBBtn,
  MDBContainer,
  MDBCard,
  MDBInput,
  MDBRating,
  MDBTable,
  MDBRow,
  MDBCol,
} from "mdbreact";

class AddComment extends React.Component {
  state = {
    comment: {
      title: "",
      message: "",
    },
    score: [],
  };

  postComment = async () => {
    console.log("in");
    const res = await axios.post(
      "http://localhost:3000/comments",
      this.comment
    );

    console.log(res);
    this.state = { ...res };
  };

  handleSubmit = async (e) => {
    e.preventDefault();

    const { addComment } = this.props;
    addComment({
      _id: null,
      product_id: this.props.product_id,
      user_id: 2,
      title: this.state.comment.title,
      message: this.state.comment.message,
      score: this.state.comment.score,
      date: "Added just now...",
    });

    try {
      const response = await axios.post("http://localhost:3000/comments", {
        product_id: this.props.product_id,
        user_id: 2,
        title: this.state.comment.title,
        message: this.state.comment.message,
        score: this.state.comment.score,
      });

      //save and alert
      alert(response.data);
    } catch (e) {
      alert(e);
    }
  };

  handleTextChange = (e) => {
    const comment = { ...this.state.comment };
    comment[e.currentTarget.name] = e.currentTarget.value;
    this.setState({ comment });
    console.log(comment);
  };

  handleRateChange = (e) => {
    const score = this.state.score;
    score[e.currentTarget.name] = e.currentTarget.v - model;
    this.setState({ score });
    console.log(score);
  };

  render() {
    return (
      <MDBCard className="mt-5">
        <h1 className=" position-relative text-center">הוסף תגובה</h1>
        <MDBTable className="ml-5">
          <MDBRow>
            <MDBInput
              type="text"
              ref={this.title}
              name="title"
              value={this.state.comment.title}
              onChange={this.handleTextChange}
            ></MDBInput>
          </MDBRow>
          <MDBRow>
            <MDBInput
              type="text"
              ref={this.title}
              name="message"
              value={this.state.comment.message}
              onChange={this.handleTextChange}
            ></MDBInput>
          </MDBRow>
          {this.props.commentsCategories.map((category) => (
            <MDBRow>
              <p>{category}</p>
              <MDBRating
                ref={this.state.score[category]}
                name={category}
                submit={this.handleRateChange}
              ></MDBRating>
            </MDBRow>
          ))}
          <MDBRow>
            <div className="text-center">
              <MDBBtn className="text-center" onClick={this.handleSubmit}>
                הגב
              </MDBBtn>
            </div>
          </MDBRow>
        </MDBTable>
      </MDBCard>
    );
  }
}

export default AddComment;
