import React, { Component } from "react";
import axios from "axios";
import _ from "lodash";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBCol,
  MDBTable,
} from "mdbreact";
import "../styles/ImagesWithCards.css";

class CardExample extends Component {
  state = {
    allProducts: [],
    products: [],
    allCompenies: [],
    compenies: [],

    initQuery: {
      ish: ["indica", "sativa", "hybrid"],
      thc: ["15", "20"],
      allCompenies: [],
    },
    query: {
      ishFirstClick: true,
      thcFirstClick: true,
      companyFirstClick: true,
      ish: ["indica", "sativa", "hybrid"],
      thc: ["15", "20"],
      compenies: [],
    },
  };

  async componentDidMount() {
    const allProducts = await axios.get("http://localhost:3000/products/all");
    await this.setState({
      allProducts: allProducts.data,
      products: allProducts.data,
    });

    const allCompenies = await axios.get("http://localhost:3000/compenies");
    await this.setState({
      allCompenies: allCompenies.data,
      compenies: allCompenies.data,
    });
  }

  ish_btnClicked = async (e) => {
    if (this.state.query.ishFirstClick) {
      this.state.products = this.state.allProducts;
      this.state.query.ish = [];
      this.state.query.ishFirstClick = false;
    }

    this.state.query.ish.indexOf(e.currentTarget.name) === -1
      ? this.state.query.ish.push(e.currentTarget.name)
      : _.remove(this.state.query.ish, (item) => {
          return item == e.currentTarget.name;
        });

    if (this.state.query.ish.length == 0) {
      this.state.query.ish = this.state.initQuery.ish;
      this.state.query.ishFirstClick = true;
    }

    console.log(this.state.query.ish);
    this.generateProductList();
  };

  thc_btnClicked = async (e) => {
    if (this.state.query.thcFirstClick) {
      this.state.products = this.state.allProducts;
      this.state.query.thc = [];
      this.state.query.thcFirstClick = false;
    }

    this.state.query.thc.indexOf(e.currentTarget.name) === -1
      ? this.state.query.thc.push(e.currentTarget.name)
      : _.remove(this.state.query.thc, (item) => {
          return item == e.currentTarget.name;
        });

    if (this.state.query.thc.length == 0) {
      this.state.query.thc = this.state.initQuery.thc;
      this.state.query.thcFirstClick = true;
    }

    console.log(this.state.query.thc);
    this.generateProductList();
  };

  company_btnClicked = async (e) => {
    if (this.state.query.companyFirstClick) {
      // this.state.compenies = this.state.allCompenies;
      this.state.query.compenies = [];
      this.state.query.thcFirstClick = false;
    }

    this.state.query.compenies.indexOf(e.currentTarget.name) === -1
      ? this.state.query.compenies.push(e.currentTarget.name)
      : _.remove(this.state.query.compenies, (item) => {
          return item == e.currentTarget.name;
        });

    if (this.state.query.compenies.length == 0) {
      this.state.query.compenies = this.state.allCompenies;
      this.state.query.companyFirstClick = true;
    }

    console.log(this.state.query.compenies);
    this.generateProductList();
  };

  generateProductList = () => {
    var productsAfterQuery = this.state.allProducts.filter(
      (product) =>
        this.state.query.ish.includes(product.ish) &&
        this.state.query.thc.includes(product.thc) &&
        this.state.query.compenies.includes(product.company)
    );

    this.setState({ products: productsAfterQuery });
  };

  render() {
    console.log(this.state.query.compenies);
    return (
      <div id="images_with_cards">
        <h1 className="h1-responsive font-weight-bold text-center">
          המוצרים המומצלים ביותר
        </h1>
        <MDBTable>
          <MDBRow>
            <MDBCol id="all_products" size="8">
              <MDBTable>
                <MDBRow className="border">
                  {this.state.products.map((product) => (
                    <MDBCol>
                      <MDBCard id="card">
                        <MDBCardImage
                          className="img-fluid"
                          src={product.BoxImg}
                        />
                        <MDBCardBody cascade>
                          <MDBCardTitle>{product.name}</MDBCardTitle>
                          <MDBCardText id="description">
                            {product.productDescription}
                          </MDBCardText>
                          <MDBBtn id="btn" href={"/product/" + product._id}>
                            לצפייה
                          </MDBBtn>
                        </MDBCardBody>
                      </MDBCard>
                    </MDBCol>
                  ))}
                </MDBRow>
              </MDBTable>
            </MDBCol>

            <MDBCol size="3" className="border">
              <div>
                <MDBCard id="card">
                  <MDBTable>
                    <MDBRow>
                      <MDBCol className="align-middle">
                        <h1 className="black-text">סנן מוצרים</h1>
                      </MDBCol>
                    </MDBRow>
                    <MDBRow>
                      <MDBCol>
                        <MDBBtn
                          className="btn-sm"
                          name="sativa"
                          onClick={this.ish_btnClicked}
                        >
                          סאטיבה
                        </MDBBtn>
                      </MDBCol>
                      <MDBCol>
                        <MDBBtn
                          className="btn-sm"
                          name="indica"
                          onClick={this.ish_btnClicked}
                        >
                          אינדיקה
                        </MDBBtn>
                      </MDBCol>
                      <MDBCol>
                        <MDBBtn
                          className="btn-sm"
                          name="hybrid"
                          onClick={this.ish_btnClicked}
                        >
                          הייבריד
                        </MDBBtn>
                      </MDBCol>
                    </MDBRow>
                    <MDBRow>
                      <MDBCol>
                        <MDBBtn
                          className="btn-sm"
                          name="20"
                          onClick={this.thc_btnClicked}
                        >
                          THC - 20
                        </MDBBtn>
                      </MDBCol>
                      <MDBCol>
                        <MDBBtn
                          className="btn-sm"
                          name="15"
                          onClick={this.thc_btnClicked}
                        >
                          THC - 15
                        </MDBBtn>
                      </MDBCol>
                    </MDBRow>
                    <MDBRow>
                      {this.state.allCompenies.map((company) => (
                        <MDBCol>
                          <MDBBtn
                            className="btn-sm"
                            name={company.name}
                            onClick={this.company_btnClicked}
                          >
                            {company.name}
                          </MDBBtn>
                        </MDBCol>
                      ))}
                    </MDBRow>
                  </MDBTable>
                </MDBCard>
              </div>
            </MDBCol>
          </MDBRow>
        </MDBTable>
      </div>
    );
  }
}

export default CardExample;
