import React, { Component } from "react";
import CarouselWithCaprion from "../components/CarouselWithCaprion";
import axios from "axios";
import ProductView from "../components/ProductView";
import SocialNewsFeed from "../components/SocialNewsFeed";
import "./ProductPage.css";

class ProductPage extends Component {
  state = {
    product: {},
  };

  async componentDidMount() {
    const product = await axios.get(
      `http://localhost:3000/products/${this.props.match.params.id}`
    );

    this.setState({ product: product.data[0] });
  }

  render() {
    return (
      <div id="productpage">
        <MDBView>
          <h1>{this.state.product.name}</h1>
          <CarouselWithCaprion {...this.state.product} />
          <ProductView {...this.state.product} />
          <SocialNewsFeed {...this.state.product} />
        </MDBView>
      </div>
    );
  }
}

export default ProductPage;
