import React from "react";
import { MDBRow, MDBCard, MDBCardBody } from "mdbreact";
import SingleSocialComment from "./SingleSocialComment";
import axios from "axios";
import AddComment from "./AddComment";

class SocialNewsFeed extends React.Component {
  constructor(props) {
    super(props);

    this.state = { comments: [] };
  }

  // //need to change dynamicly
  // async componentDidMount() {
  //   await this.getComments("5f5befa2a630fd4da0f2707f");
  // }

  getComments = async (product_id) => {
    var url = `http://localhost:3000/comments/byProduct/${product_id}`;
    const comments = await axios.get(url);
    return comments;
    // await this.setState({ comments: comments.data });
  };

  render() {
    if (this.props.product_id != undefined) {
      this.getComments(this.props.product_id).then((comments) => {
        console.log(comments.data);
      });
    } else {
      console.log("first");
      return <div>no comments</div>;
    }
  }
}

export default SocialNewsFeed;
