import React from "react";
import { MDBRow, MDBCard, MDBCardBody, MDBIcon } from "mdbreact";
import SingleSocialComment from "./SingleSocialComment";
import axios from "axios";

class SocialSingleComment extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      comments: [],
    };
  }

  //need to change dynamicly
  componentDidMount() {
    this.getComments(1);
  }

  getComments = async (product_id) => {
    var url = `http://localhost:3000/comments/byProduct/${product_id}`;
    const comments = await axios.get(url);

    this.setState({ comments: comments.data });
  };

  render() {
    const v = this.state.comments[this.state.comments.length - 1];

    return (
      <div>
        <MDBCard
          className="my-5 px-5 pt-4"
          style={{ fontWeight: 300, maxWidth: 600 }}
        >
          {this.state.comments.map((comment) => (
            <MDBCardBody className="py-0">
              <MDBRow>
                <SingleSocialComment {...comment} />
              </MDBRow>
            </MDBCardBody>
          ))}
        </MDBCard>
      </div>
    );
  }
}

export default SocialSingleComment;
exports.refresh = getComments;
