import React, { Component } from "react";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBCol,
  MDBTable,
  MDBRating,
} from "mdbreact";
import axios from "axios";

class StatisticsForProducts extends Component {
  state = {
    rates: [
      { category: "Taste", score: 5 },
      { category: "Smell", score: 4 },
      { category: "size", score: 1 },
    ],
    comments: [],
    fetch: true,
  };

  getComments = async (product_id) => {
    var url = `http://localhost:3000/comments/byProduct/${product_id}`;
    const comments = await axios.get(url);
    await this.setState({ comments: comments.data, fetch: false });
  };

  render() {
    if (this.props.product_id !== undefined && this.state.fetch) {
      this.getComments(this.props.product_id);
    }
    return (
      <div>
        <MDBCard>
          <MDBTable>
            <MDBRow>
              <p className="text-center">סטטיסטיקות</p>
            </MDBRow>
            {this.state.rates
              .sort((a, b) => a.score >= b.score)
              .map((rate) => (
                <div className="ml-5">
                  <MDBRow>
                    <MDBCardTitle>{rate.category}</MDBCardTitle>
                    <MDBRating
                      fillColors={[
                        "red-text",
                        "orange-text",
                        "yellow-text",
                        "lime-text",
                        "light-green-text",
                      ]}
                      data={[
                        {
                          tooltip: "Bad",
                          choosed: rate.score >= 1 && rate.score < 2,
                        },
                        {
                          tooltip: "Poor",
                          choosed: rate.score >= 2 && rate.score < 3,
                        },
                        {
                          tooltip: "Ok",
                          choosed: rate.score >= 3 && rate.score < 4,
                        },
                        {
                          tooltip: "Nice",
                          choosed: rate.score >= 4 && rate.score < 5,
                        },
                        {
                          tooltip: "Excellent",
                          choosed: rate.score == 5,
                        },
                      ]}
                    />
                  </MDBRow>
                </div>
              ))}
          </MDBTable>
        </MDBCard>
      </div>
    );
  }
}

export default StatisticsForProducts;
