import React, { Component } from "react";
import { MDBRow, MDBCard, MDBCardBody, MDBIcon } from "mdbreact";

class SingleSocialComment extends Component {
  state = {};
  render() {
    return (
      <div className="mdb-feed">
        <div className="news">
          <div className="label">
            <img
              src="https://mdbootstrap.com/img/Photos/Avatars/avatar-1-mini.jpg"
              alt=""
              className="rounded-circle z-depth-1-half"
            />
          </div>
          <div className="excerpt">
            <div className="brief">
              <a href="#!" className="name">
                comments for {this.props._id}
              </a>{" "}
              added you as a friend
              <div className="date">1 hour ago</div>
            </div>
            <div className="feed-footer">
              <a href="#!" className="like">
                <MDBIcon icon="heart" />
                <span>5 likes</span>
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default SingleSocialComment;
