import htttService from "./httpService";

export async function signIn(email, password) {
  var ans = false;

  const { data: jwt } = await htttService.signIn(email, password);
  if (jwt) {
    localStorage.setItem("token", jwt);
    ans = true;
  }

  return ans;
}

export async function signUp(firstName, email, password, phone) {
  var ans = false;

  const { data: jwt } = await htttService.signUp(
    firstName,
    email,
    password,
    phone
  );
  if (jwt) {
    localStorage.setItem("token", jwt);
    ans = true;
  }

  return ans;
}

export default {
  signIn,
  signUp,
};
