import React, { Component } from "react";
import {
  MDBCard,
  MDBIcon,
  MDBRating,
  MDBRow,
  MDBTable,
  MDBCardBody,
  MDBCardTitle,
  MDBCol,
} from "mdbreact";
import ProgressBar from "react-customizable-progressbar";

class SingleSocialComment extends Component {
  state = {};

  getProgressBarColor = (ratio) => {
    console.log(ratio);
    switch (Math.floor(ratio)) {
      case 0:
        return "red";
      case 1:
        return "orange";
      case 2:
        return "yellow";
      case 3:
        return "blue";
      case 4:
        return "green";
    }
  };

  render() {
    return (
      <MDBCard className="mdb-feed py-3 px-md-5">
        <div className="news">
          <div className="label">
            <img
              src="https://i.imgur.com/eLoV5gE.png"
              alt=""
              className="rounded-circle z-depth-1-half"
            />
          </div>
          <div className="excerpt">
            <div className="brief">
              <a href="#!" className="name">
                User id : {this.props.user_id}
              </a>{" "}
              <MDBCardTitle className="date">{this.props.title}</MDBCardTitle>
              <MDBCardBody className="date">{this.props.message}</MDBCardBody>
            </div>
            <div id="scores">
              <MDBRow>
                {this.props.score
                  .sort((a, b) => a.value >= b.value)
                  .map((scoreElement, index) => (
                    <MDBCol key={index}>
                      <p>{scoreElement.title}</p>
                      <ProgressBar
                        radius={20}
                        progress={scoreElement.value}
                        steps={5}
                        strokeWidth={4}
                        strokeColor={this.getProgressBarColor(
                          scoreElement.value
                        )}
                        trackStrokeWidth={4}
                        pointerRadius={4}
                        pointerStrokeWidth={2}
                        pointerStrokeColor="black"
                        initialAnimationDelay={500}
                        initialAnimation={true}
                      ></ProgressBar>
                    </MDBCol>
                  ))}
              </MDBRow>
            </div>

            <div>{this.props.date.substring(0, 10)}</div>
          </div>
        </div>
      </MDBCard>
    );
  }
}

export default SingleSocialComment;
