import React, { Component } from "react";
import axios from "axios";
import {
  MDBBtn,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBCol,
  MDBTable,
} from "mdbreact";
import "../styles/ImagesWithCards.css";

class CardExample extends Component {
  state = {
    products: [],
  };

  async componentDidMount() {
    const products = await axios.get("http://localhost:3000/products/all");
    this.setState({ products: products.data });
  }

  render() {
    return (
      <div id="images_with_cards">
        <h1 className="h1-responsive font-weight-bold text-center">
          המוצרים המומצלים ביותר
        </h1>
        <MDBTable>
          <MDBRow>
            <MDBCol id="all_products" size="8">
              <MDBTable>
                <MDBRow className="border">
                  {this.state.products.map((product) => (
                    <MDBCol>
                      <MDBCard id="card">
                        <MDBCardImage
                          className="img-fluid"
                          src={product.BoxImg}
                        />
                        <MDBCardBody cascade>
                          <MDBCardTitle>{product.name}</MDBCardTitle>
                          <MDBCardText id="description">
                            {product.productDescription}
                          </MDBCardText>
                          <MDBBtn id="btn" href={"/product/" + product._id}>
                            לצפייה
                          </MDBBtn>
                        </MDBCardBody>
                      </MDBCard>
                    </MDBCol>
                  ))}
                </MDBRow>
              </MDBTable>
            </MDBCol>
            <MDBCol size="3" className="border">
              <div>
                <MDBCard>
                  <h1 className="ceter-text black-text"></h1>
                </MDBCard>
              </div>
            </MDBCol>
          </MDBRow>
        </MDBTable>
      </div>
    );
  }
}

export default CardExample;
