import React, { Component } from "react";
import axios from "axios";

class LogIn extends Component {
  email = React.createRef();
  password = React.createRef();

  state = {
    account: {
      email: "",
      password: "",
    },
  };

  handleSubmit = async (e) => {
    e.preventDefault();

    const token = await axios.post("http://localhost:3000/auth", {
      email: this.email.current.value,
      password: this.password.current.value,
    });

    //save and alert
    this.state.token = token.data;
    alert(token.data);
  };

  handleChange = (e) => {
    const account = { ...this.state.account };
    account[e.currentTarget.name] = e.currentTarget.value;
    this.setState({ account });
  };

  render() {
    return (
      <div>
        <form className="border border-light p-5" onSubmit={this.handleSubmit}>
          <p className="h4 mb-4 text-center">Sign in</p>

          <input
            type="email"
            id="defaultLoginFormEmail"
            className="form-control mb-4"
            placeholder="E-mail"
            ref={this.email}
            name="email"
            value={this.state.account.email}
          />

          <input
            type="password"
            id="defaultLoginFormPassword"
            className="form-control mb-4"
            placeholder="Password"
            ref={this.password}
            name="password"
            value={this.state.account.password}
          />

          <div className="d-flex justify-content-between">
            <div>
              <div className="custom-control custom-checkbox">
                <input
                  type="checkbox"
                  className="custom-control-input"
                  id="defaultLoginFormRemember"
                />
                <label
                  className="custom-control-label"
                  htmlFor="defaultLoginFormRemember"
                >
                  Remember me
                </label>
              </div>
            </div>
            <div>
              <a href="">Forgot password?</a>
            </div>
          </div>

          <button className="btn btn-info btn-block my-4" type="submit">
            Sign in
          </button>

          <div className="text-center">
            <p>
              Not a member?
              <a href="">Register</a>
            </p>

            <p>or sign in with:</p>
            <a type="button" className="white-text mx-2">
              <i className="fab fa-facebook-f"></i>
            </a>
            <a type="button" className="white-text mx-2">
              <i className="fab fa-twitter"></i>
            </a>
            <a type="button" className="white-text mx-2">
              <i className="fab fa-linkedin-in"></i>
            </a>
            <a type="button" className="white-text mx-2">
              <i className="fab fa-github"></i>
            </a>
          </div>
        </form>
      </div>
    );
  }
}

export default LogIn;
